import React, { useEffect, useRef, useState } from "react";
import "./CandidatesDashboardStyles.css";
import contact from "../../Assets/contact.png"
import attendance from "../../Assets/leaves.png"
import employees from "../../Assets/employees.png"
import logout from "../../Assets/logout.png"
import leaves from "../../Assets/leaves.png"
import AddNewCandidate from "./AddNewCandidate";

const Sidebar = ()=>{
    const[show,setShow]=useState(false);
    const modalRef = useRef();
    console.log(show);

    useEffect(() => {
        const handleClickOutside = (event) => {
          if (modalRef.current && !modalRef.current.contains(event.target)) {
            setShow(false);
          }
        };

        // if (show) {
        //   document.addEventListener("mousedown", handleClickOutside);
        //   console.log("mousePressed")
        // }

        // return () => {
        //   document.removeEventListener("mousedownk", handleClickOutside);
        // };

        if(show){
            document.addEventListener("mousedown", (e) => {
                handleClickOutside
              });

              document.addEventListener("click", (e) => {
                console.log("Click happened (mouse press + release)");
              });
        }
      }, [show]);

    return(
        <div className="sidebar">
            <div className="logo">LOGO</div>
            <input className="sidebar-search" type="text" placeholder="Search" />

            <h4 className="section">Recruitment</h4>
            <div className="nav-item" onClick={()=>setShow(!show)}>
            <img src={contact}/>
            <span>Candidates</span>
            </div>

            {
                show ? (
                    <AddNewCandidate/>
                ) : (<div>/</div>)
            }

            <h4 className="section">Organization</h4>

            <div className="nav-item">
                <img src={employees}/>
                <span>Employees</span>
            </div>

            <div className="nav-item">
            <img src={attendance}/>
            <span>Attendance</span>
            </div>

            <div className="nav-item">
            <img src={leaves}/>
            <span>Leaves</span>
            </div>

            <h4 className="section">Others</h4>
            <div className="nav-item">
            <img src={logout}/>
            <span>Logout</span>
            </div>
        </div>
    )
}

export default Sidebar;
