import React, { useEffect, useRef, useState } from "react";
import Sidebar from "../Candidates/Sidebar";
import msg from '../../Assets/MailIcon.png'
import notification from '../../Assets/notifications.png'
import downIcon from "../../Assets/DownIcon.png"
import ellipse from "../../Assets/ellipse.png"
import threeDots from "../../Assets/threeDotsIcon.png"
import AttendanceDropDown from "./AttendanceDropDown"
import axios from "axios";


const Attendance=()=>{
    const [openIndex, setOpenIndex] = useState(null);
    const [employees, setEmployees] = useState([]);

    useEffect(() => {
      const fetchEmployees = async () => {
        try {
          const token = localStorage.getItem('token'); 

          const res = await axios.get('http://localhost:8000/api/employees', {
            headers: {
              Authorization: `Bearer ${token}`
            }
          });

          setEmployees(res.data);
        } catch (err) {
          console.error('Error fetching employees:', err.response?.data || err.message);
        }
      };

      fetchEmployees();
    }, []);

  return (
    <div className="dashboard">
      <Sidebar/>
      <div className="main">

        <div className="header">
          <h2>Attendance</h2>
          <div>
            <img src={msg}/>
            <img src={notification}/>
            <img src={ellipse}/>
            <img src={downIcon}/>
          </div>
        </div>

        <div className="filters">
          <div>
            <select>
              <option>Status <img src={downIcon}/></option>
            </select>
          </div>

          <div className="header-actions">
            <input type="text" placeholder="Search" />
          </div>
        </div>

        <div className="table-wrapper">
          <table>
            <thead>
              <tr>
                <th>Profile</th>
                <th>Employee Name</th>
                <th>Position</th>
                <th>Department</th>
                <th>Task</th>
                <th>Status</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {employees.map((c, i) => (
                <tr key={c.id}>
                  <td><img src={ellipse}/></td>
                  <td>{c.name}</td>
                  <td>{c.email}</td>
                  <td>{c.phone}</td>
                  <td>{c.position}</td>
                  <td onClick={() => setOpenIndex(c.id === openIndex ? null : c._id)}>

                  <td>
                    <AttendanceDropDown
                      // currentStatus={c.status.charAt(0).toUpperCase() + c.status.slice(1)}
                      onChange={(newStatus) => {
                        // Update candidate status via API
                        c.status=newStatus
                        // updateCandidateAttendance(c._id, newStatus);
                      }}
                    />
                  </td>

                    {
                        openIndex===c.id && (
                            <div className="dropDownPos">
                                <div className="dropdown-item-pos">Intern</div>
                                <div className="dropdown-item-pos">Full-time</div>
                                <div className="dropdown-item-pos">Junior</div>
                                <div className="dropdown-item-pos">Senior</div>
                                <div className="dropdown-item-pos">Team Lead</div>
                            </div>
                        )
                    }

                  </td>

                  <td style={{position: "relative"}}>
                    <img
                      src={threeDots}
                      className="cursor-pointer"
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Attendance;
