import React, { useState } from "react";
import "./AddNewCandidate.css"
import axios from 'axios';

const AddNewCandidate=({show,setShow,modalRef})=>{
    const [formData, setFormData] = useState({
      name: '',
      email: '',
      phone: '',
      resume: null
    });

    const handleChange = (e) => {
      if (e.target.name === 'resume') {
        setFormData({ ...formData, resume: e.target.files[0] });
      } else {
        setFormData({ ...formData, [e.target.name]: e.target.value });
      }
    };

    const handleSubmit = async (e) => {
      e.preventDefault();

      try {
        const data = new FormData();
        data.append('name', formData.name);
        data.append('email', formData.email);
        data.append('phone', formData.phone);
        data.append('resume', formData.resume);

        const res = await axios.post('http://localhost:8000/api/candidates', data, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });

        console.log('Candidate created:', res.data);
        alert('Candidate created successfully!');
      } catch (err) {
        console.error('Error creating candidate:', err.response?.data || err.message);
        alert('Error submitting form');
      }
    };


    return(
        <div className="popup-overlay">
        <div className="popup-container" ref={modalRef}>
            <div className="popup-header">
              <h4>Add New Candidate</h4>
              <button className="close-btn" onClick={() => setShow(false)}>Ã—</button>
            </div>
          <form className="popup-form">
            <div className="form-row">
              <input type="text" placeholder="Full Name*" required onChange={handleChange}/>
              <input type="email" placeholder="Email Address*" required />
            </div>

            <div className="form-row">
              <input type="tel" placeholder="Phone Number*" required onChange={handleChange}/>
              <input type="text" placeholder="Position*" required onChange={handleChange} />
            </div>

            <div className="form-row">
              <input type="text" placeholder="Experience*" required onChange={handleChange} />
              <div className="resume-upload">
                <input type="file" id="resume" hidden onChange={handleChange} accept=".pdf , .doc , .docx"/>
                <label htmlFor="resume">Resume*</label>
              </div>
            </div>

            <div className="declaration">
              <input type="checkbox" id="declaration" />
              <label htmlFor="declaration">
                I hereby declare that the above information is true to the best of my knowledge and belief
              </label>
            </div>

            <button type="submit" className="submit-btn" onSubmit={handleSubmit}>
              Save
            </button>
          </form>
        </div>
        </div>
    )
}

export default AddNewCandidate;
