import React, { useRef, useState } from "react";
import "./Employees.css";
import Sidebar from "../Candidates/Sidebar";
import msg from '../../Assets/MailIcon.png'
import notification from '../../Assets/notifications.png'
import downIcon from "../../Assets/DownIcon.png"
import ellipse from "../../Assets/ellipse.png"
import threeDots from "../../Assets/threeDotsIcon.png"
import ThreeDotMenu from "./ThreeDotMenu";
import EditEmployee from "./EditEmployee";

const candidates = [
  {
    id: 1,
    name: "Jacob William",
    email: "jacob.william@example.com",
    phone: "(252) 555-0111",
    position: "Senior Developer",
    status: "new",
    experience: "1+"
  },
  {
    id: 2,
    name: "Guy Hawkins",
    email: "kenzi.lawson@example.com",
    phone: "(907) 555-0101",
    position: "Human Resource Intern",
    status: "new",
    experience: "0"
  },
  {
    id: 3,
    name: "Arlene McCoy",
    email: "arlene.mccoy@example.com",
    phone: "(302) 555-0107",
    position: "Full Time Designer",
    status: "selected",
    experience: "2"
  },
  {
    id: 4,
    name: "Leslie Alexander",
    email: "willie.jennings@example.com",
    phone: "(207) 555-0119",
    position: "Full Time Developer",
    status: "rejected",
    experience: "0"
  }
];

const Employees=()=>{
  const[sidebarOpen , setSidebarOpen]=useState(false);
  const [openMenuId, setOpenMenuId] = useState(null);

  const handleToggleMenu = (id) => {
    setOpenMenuId(prevId => (prevId === id ? null : id));
  };

  const[show,setShow]=useState(false);
  const modalRef = useRef();

  useEffect(() => {
      const handleClickOutside = (event) => {
        if (modalRef.current && !modalRef.current.contains(event.target)) {
          setShow(false);
        }
      };

      if (show) {
        document.addEventListener("mousedown", handleClickOutside);
        console.log("mousePressed");
      }

      return () => {
        document.removeEventListener("mousedown", handleClickOutside);
      };
    }, [show]);


  return (
    <div className="dashboard">
      <Sidebar/>
      <div className="main">

        <div className="header">
          <h2>Employees</h2>
          <div>
            <img src={msg}/>
            <img src={notification}/>
            <img src={ellipse}/>
            <img src={downIcon}/>
          </div>
        </div>

        <div className="filters">
          <div>
            <select>
              <option>Position</option>
            </select>
          </div>

          <div className="header-actions">
            <input type="text" placeholder="Search" />
          </div>

        </div>

        <div className="table-wrapper">
          <table>
            <thead>
              <tr>
                <th>Sr no.</th>
                <th>Employee Name</th>
                <th>Email Address</th>
                <th>Phone Number</th>
                <th>Position</th>
                <th>Department</th>
                <th>Date of Joining</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {candidates.map((c, i) => (
                <tr key={c.id}>
                  <td><img src={ellipse}/></td>
                  <td>{c.name}</td>
                  <td>{c.email}</td>
                  <td>{c.phone}</td>
                  <td>{c.position}</td>
                  <td>
                    <button className={`status ${c.status}`}>
                      {c.status.charAt(0).toUpperCase() + c.status.slice(1)}
                    </button>
                  </td>
                  <td>{c.experience}</td>
                  <td>
                  <ThreeDotMenu
                    isOpen={openMenuId === c.id}
                    onToggle={() => handleToggleMenu(c.id)}
                    onEdit={()=> setShow(!show)}
                    onDelete={() => console.log("Delete", c.id)}
                  />
                  </td>

                  {
                      show && (
                          <EditEmployee show={show} setShow={setShow} modalRef={modalRef}/>
                      )
                  }

                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Employees;
