import React, { useEffect, useRef, useState } from "react";
import "./Employees.css";
import Sidebar from "../Candidates/Sidebar";
import msg from '../../Assets/MailIcon.png'
import notification from '../../Assets/notifications.png'
import downIcon from "../../Assets/DownIcon.png"
import ellipse from "../../Assets/ellipse.png"
import threeDots from "../../Assets/threeDotsIcon.png"
import ThreeDotMenu from "./ThreeDotMenu";
import EditEmployee from "./EditEmployee";
import axios from "axios";

const candidates = [
  {
    id: 1,
    name: "Jacob William",
    email: "jacob.william@example.com",
    phone: "(252) 555-0111",
    position: "Senior Developer",
    status: "new",
    experience: "1+"
  },
  {
    id: 2,
    name: "Guy Hawkins",
    email: "kenzi.lawson@example.com",
    phone: "(907) 555-0101",
    position: "Human Resource Intern",
    status: "new",
    experience: "0"
  },
  {
    id: 3,
    name: "Arlene McCoy",
    email: "arlene.mccoy@example.com",
    phone: "(302) 555-0107",
    position: "Full Time Designer",
    status: "selected",
    experience: "2"
  },
  {
    id: 4,
    name: "Leslie Alexander",
    email: "willie.jennings@example.com",
    phone: "(207) 555-0119",
    position: "Full Time Developer",
    status: "rejected",
    experience: "0"
  }
];

const Employees=()=>{
  const[sidebarOpen , setSidebarOpen]=useState(false);
  const[isOpen,setIsOpen] = useState(null);

  const[show,setShow]=useState(false);
  const modalRef = useRef();

  useEffect(() => {
      const handleClickOutside = (event) => {
        if (modalRef.current && !modalRef.current.contains(event.target)) {
          setShow(false);
        }
      };

      if (show) {
        document.addEventListener("mousedown", handleClickOutside);
        console.log("mousePressed");
      }

      return () => {
        document.removeEventListener("mousedown", handleClickOutside);
      };
    }, [show]);

    const [employees, setEmployees] = useState([]);

    useEffect(() => {
      const fetchEmployees = async () => {
        try {
          const token = localStorage.getItem('token'); 

          const res = await axios.get('http://localhost:8000/api/employees', {
            headers: {
              Authorization: `Bearer ${token}`
            }
          });

          setEmployees(res.data);
        } catch (err) {
          console.error('Error fetching employees:', err.response?.data || err.message);
        }
      };

      fetchEmployees();
    }, []);

    const handleDelete = async (id) => {
      const confirmDelete = window.confirm("Are you sure you want to delete this candidate?");
      if (!confirmDelete) return;

      try {
        const token = localStorage.getItem("token");

        await axios.delete(`http://localhost:8000/api/employees/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        alert("Candidate deleted successfully");
        window.location.reload();
      } catch (err) {
        console.error("Delete error:", err.message);
        alert("Failed to delete candidate");
      }
    };

  return (
    <div className="dashboard">
      <Sidebar/>
      <div className="main">

        <div className="header">
          <h2>Employees</h2>
          <div>
            <img src={msg}/>
            <img src={notification}/>
            <img src={ellipse}/>
            <img src={downIcon}/>
          </div>
        </div>

        <div className="filters">
          <div>
            <select>
              <option>Position</option>
            </select>
          </div>

          <div className="header-actions">
            <input type="text" placeholder="Search" />
          </div>

        </div>

        <div className="table-wrapper">
          <table>
            <thead>
              <tr>
                <th>Sr no.</th>
                <th>Employee Name</th>
                <th>Email Address</th>
                <th>Phone Number</th>
                <th>Position</th>
                <th>Department</th>
                <th>Date of Joining</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {employees.map((c, i) => (
                <tr key={c.id}>
                  <td><img src={ellipse}/></td>
                  <td>{c.name}</td>
                  <td>{c.email}</td>
                  <td>{c.phone}</td>
                  <td>{c.position}</td>
                  <td>{c.department}</td>

                  <td>
                    {/* <button className={`status ${c.status}`}>
                      {c.department.charAt(0).toUpperCase() + c.status.slice(1)}
                    </button> */}
                  </td>
                  <td style={{position: "relative"}}>
                    <img
                      src={threeDots}
                      onClick={() => setIsOpen(isOpen === null ? c.id : null)}
                      className="cursor-pointer"
                    />
                    {isOpen === c._id && (
                      <div className="dropdown">
                        <div className="dropdown-item" onClick={()=> {setShow(!show); setIsOpen(null)}}>Edit</div>
                        <div className="dropdown-item" onClick={()=> {handleDelete(c._id)}}>Delete</div>
                      </div>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {
          show && (
            <EditEmployee show={show} setShow={setShow} modalRef={modalRef}/>
          )
        }
      </div>
    </div>
  );
}

export default Employees;
