import React, { useEffect, useRef, useState } from "react";
import "./CandidatesDashboardStyles.css";
import Sidebar from "./Sidebar";
import msg from '../../Assets/MailIcon.png'
import notification from '../../Assets/notifications.png'
import downIcon from "../../Assets/DownIcon.png"
import ellipse from "../../Assets/ellipse.png"
import threeDots from "../../Assets/threeDotsIcon.png"
import AddNewCandidate from "./AddNewCandidate";
import axios from "axios";

// const candidates = [
//   {
//     id: 1,
//     name: "Jacob William",
//     email: "jacob.william@example.com",
//     phone: "(252) 555-0111",
//     position: "Senior Developer",
//     status: "new",
//     experience: "1+"
//   },
//   {
//     id: 2,
//     name: "Guy Hawkins",
//     email: "kenzi.lawson@example.com",
//     phone: "(907) 555-0101",
//     position: "Human Resource Intern",
//     status: "new",
//     experience: "0"
//   },
//   {
//     id: 3,
//     name: "Arlene McCoy",
//     email: "arlene.mccoy@example.com",
//     phone: "(302) 555-0107",
//     position: "Full Time Designer",
//     status: "selected",
//     experience: "2"
//   },
//   {
//     id: 4,
//     name: "Leslie Alexander",
//     email: "willie.jennings@example.com",
//     phone: "(207) 555-0119",
//     position: "Full Time Developer",
//     status: "rejected",
//     experience: "0"
//   }
// ];

const CandidatesDashboard=()=>{
  const[show,setShow]=useState(false);
  const modalRef = useRef();

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        setShow(false);
      }
    };

    if (show) {
      document.addEventListener("mousedown", handleClickOutside);
      console.log("mousePressed");
    }

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [show]);

  const[isOpen,setIsOpen] = useState(null);

  const [candidates, setCandidates] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchCandidates = async () => {
    try {
      const token = localStorage.getItem('token'); // ðŸ” Get token

      const res = await axios.get('http://localhost:8000/api/candidates', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      setCandidates(res.data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching candidates:', err.response?.data || err.message);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCandidates();
  }, []);

  const downloadResume = (id) =>
    API.get(`/${id}/resume`, { responseType: 'blob' });

  const handleDownload = async (candidate) => {
    try {
      const res = await downloadResume(candidate._id);

      // create a Blob and object URL for the file
      const blob = new Blob([res.data], { type: res.data.type });
      const url = window.URL.createObjectURL(blob);

      // derive filename from Contentâ€‘Disposition header or fallback
      const cd = res.headers['content-disposition'];
      const filename = cd
        ? cd.split('filename=')[1].replace(/"/g, '')
        : `${candidate.name}_resume.pdf`;

      // create a temporary link to trigger download
      const link = document.createElement('a');
      link.href = url;
      link.download = filename;
      document.body.appendChild(link);
      link.click();
      link.remove();

      // release memory
      window.URL.revokeObjectURL(url);
    } catch (err) {
      console.error('Download error:', err.response?.data || err.message);
      alert('Failed to download resume');
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="dashboard">
      <Sidebar/>
      <div className="main">

        <div className="header">
          <h2>Candidates</h2>
          <div>
            <img src={msg}/>
            <img src={notification}/>
            <img src={ellipse}/>
            <img src={downIcon}/>
          </div>
        </div>

        <div className="filters">
          <div>
            <select>
              <option>Status</option>
            </select>
            <select>
              <option>Position</option>
            </select>
          </div>

          <div className="header-actions">
            <input type="text" placeholder="Search" />
            <button className="add-candidate" onClick={()=>setShow(!show)}>Add Candidate</button>
          </div>

          {
              show && (
                  <AddNewCandidate show={show} setShow={setShow} modalRef={modalRef}/>
              )
          }


        </div>

        <div className="table-wrapper">
          <table>
            <thead>
              <tr>
                <th>Sr no.</th>
                <th>Candidates Name</th>
                <th>Email Address</th>
                <th>Phone Number</th>
                <th>Position</th>
                <th>Status</th>
                <th>Experience</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {candidates.map((c, i) => (
                <tr key={c._id}>
                  <td>{`0${i + 1}`}</td>
                  <td>{c.name}</td>
                  <td>{c.email}</td>
                  <td>{c.phone}</td>
                  <td>{c.position}</td>
                  <td>
                    <button className={`status ${c.status}`}>
                      {c.status.charAt(0).toUpperCase() + c.status.slice(1)}
                    </button>
                  </td>
                  <td>{c.experience}</td>
                  <td style={{position: "relative"}}>
                    <img
                      src={threeDots}
                      onClick={() => setIsOpen(isOpen === null ? i : null)}
                      className="cursor-pointer"
                    />
                    {isOpen === i && (
                      <div className="dropdown">
                        <div className="dropdown-item" onClick={handleDownload(c)}>Download Resume</div>
                        <div className="dropdown-item">Delete Candidate</div>
                      </div>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default CandidatesDashboard;
